import React, { useEffect, useState } from "react";
import "../../input.css";
import "../../index.css";
import { CSSTransition } from "react-transition-group";

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);

  const drawer = () => {
    setIsOpen(!isOpen);
  };
  const handleEscKey = (e) => {
    if (e.keyCode === 27) {
      setIsOpen(false);
    }
  };
  useEffect(() => {
    if (typeof window !== "undefined") {
      if (isOpen) {
        document.body.style.setProperty("overflow", "hidden");
      } else {
        document.body.style.removeProperty("overflow");
      }

      const handleKeyDown = (e) => {
        if (e.keyCode === 27 && isOpen) {
          setIsOpen(false);
        }
      };

      document.addEventListener("keydown", handleKeyDown);

      return () => {
        document.removeEventListener("keydown", handleKeyDown);
      };
    }
  }, [isOpen]);

  return (
    <nav className="fixed w-full p-6 bg-transparent">
      <div className="flex items-center justify-between">
        {/* Mobile toggle */}
        <div className="md:hidden">
          <button onClick={drawer}>
            <svg
              className="h-8 w-8 fill-current text-black"
              fill="none"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path d="M4 6h16M4 12h16M4 18h16"></path>
            </svg>
          </button>
        </div>
        {/* Navbar */}
        <div className="hidden md:block">
          <ul className="flex space-x-8 text-sm font-sans">
            <li>
              <a href="#" className="active border-b-2 border-blue-500 pb-1">
                Home
              </a>
            </li>
            <li>
              <a href="#" className="">
                Announcement
              </a>
            </li>
            <li>
              <a href="#" className="">
                About Us
              </a>
            </li>
            <li>
              <a href="#" className="">
                Contact
              </a>
            </li>
            <li>
              <a
                href="#"
                className="cta bg-blue-500 hover:bg-blue-600 px-3 py-2 rounded text-white font-semibold"
              >
                Login
              </a>
            </li>
            <li>
              <a
                href="#"
                className="cta bg-blue-500 hover:bg-blue-600 px-3 py-2 rounded text-white font-semibold"
              >
                Sign Up
              </a>
            </li>
          </ul>
        </div>
        {/* Dark Background Transition */}
        <CSSTransition
          in={isOpen}
          classNames={{
            enter: "opacity-0",
            enterActive: "ease-out transition-medium opacity-100",
            enterDone: "opacity-100",
            exit: "opacity-100",
            exitActive: "ease-out transition-medium opacity-0",
            exitDone: "opacity-0",
          }}
          timeout={300}
          unmountOnExit
        >
          <div
            className="z-10 fixed inset-0 transition-opacity"
            onKeyDown={handleEscKey}
            tabIndex="0"
          >
            <div
              className="absolute inset-0 bg-black opacity-50"
              onClick={() => setIsOpen(false)}
            ></div>
          </div>
        </CSSTransition>
      </div>
    </nav>
  );
};

export default Header;
