import React, { useEffect, useState } from "react";
import "../../input.css";
import "../../index.css";

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);

  const drawer = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    if (typeof window !== "undefined") {
      if (isOpen) {
        document.body.style.setProperty("overflow", "hidden");
      } else {
        document.body.style.removeProperty("overflow");
      }

      const handleKeyDown = (e) => {
        if (e.keyCode === 27 && isOpen) {
          setIsOpen(false);
        }
      };

      document.addEventListener("keydown", handleKeyDown);

      return () => {
        document.removeEventListener("keydown", handleKeyDown);
      };
    }
  }, [isOpen]);

  return (
    <nav className="fixed w-full p-6 bg-transparent">
      <div className="flex items-center justify-between">
        {/* Mobile toggle */}
        <div className="md:hidden">
          <button onClick={drawer}>
            <svg
              className="h-8 w-8 fill-current text-black"
              fill="none"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path d="M4 6h16M4 12h16M4 18h16"></path>
            </svg>
          </button>
        </div>
        {/* Navbar */}
        <div className="hidden md:block">
          <ul className="flex space-x-8 text-sm font-sans">
            <li>
              <a href="#" class="active border-b-2 border-blue-500 pb-1">
                Home
              </a>
            </li>
            <li>
              <a href="#" class="">
                Services
              </a>
            </li>
            <li>
              <a href="#" class="">
                Features
              </a>
            </li>
            <li>
              <a href="#" class="">
                FAQ
              </a>
            </li>
            <li>
              <a href="#" class="">
                Contact
              </a>
            </li>
            <li>
              <a
                href="#"
                class="cta bg-blue-500 hover:bg-blue-600 px-3 py-2 rounded text-white font-semibold"
              >
                Sign Up
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Header;
